                if(null!=measureList){
                    int size = measureList.size();
                    int index = 1;
                    if(size%6==0){
                        index=size/6;
                    }else{
                        index=size/6+1;
                    }
                    for(int a=0;a<index;a++){
                        LinearLayout measureView = new LinearLayout(this);
                        measureView.setBackgroundResource(R.drawable.shape_border_bottom);
                        measureView.setLayoutParams(new LinearLayout.LayoutParams(LinearLayout.LayoutParams.MATCH_PARENT,DensityUtils.dip2px(this,60), 1f));
                        for(int k=0;k<6;k++){
                            String valueString="";
                            if(k<measureList.size()){
                                MeasuredValue measure = measureList.get(k+a*4);
                                if(null!=measure){
                                    valueString = measure.getValue();
                                }
                            }
                            TextView textView = new TextView(this);
                            textView.setGravity(Gravity.CENTER);
                            textView.setLayoutParams(new LinearLayout.LayoutParams(0, LinearLayout.LayoutParams.MATCH_PARENT, 1f));
                            textView.setText(null==valueString?"":valueString);
                            textView.setBackgroundResource(R.drawable.shape_border_rb);
                            measureView.addView(textView);
                        }
                        measureLayView.addView(measureView);
                    }
                }
